import { useEffect, useState } from "react";
import "./Pokemon.css";
import Pokemon from "./Pokemon";
import PokemonInfo from "./PokemonInfo";

function Pokedex() {
  const [pokemons, setPokemon] = useState([]);

  const [searchPokemon, setSearchPokemon] = useState("");
  const [pokemonTarget, setPokemonTarget] = useState(null);

  const loadTabPokemons = async () => {
    const response = await fetch("http://localhost:3001/pokemons", {
      withCredentials: true,
      crossorigin: true,
    });
    console.log({ response });
    const result = await response.json();
    console.log({ result });

    setPokemon(result);
  };

  useEffect(() => {
    loadTabPokemons();
  }, []);

  const filteredPokemon = pokemons.filter((p) =>
    p.name.includes(searchPokemon)
  );

  //render
  return (
    <div>
      {!pokemonTarget && (
        <div>
          <div>
            <input
              value={searchPokemon}
              onChange={(e) => setSearchPokemon(e.target.value)}
            ></input>
          </div>
          <div className="wrapperPokemon">
            {filteredPokemon.map((pokemon) => (
              <Pokemon
                key={pokemon.id}
                onClick={() => setPokemonTarget(pokemon)}
                pokemon={pokemon}
              />
            ))}
          </div>
        </div>
      )}
      {pokemonTarget && (
        <div>
          <PokemonInfo
            pokemonTarget={pokemonTarget}
            onClick={() => setPokemonTarget(pokemonTarget)}
          />
        </div>
      )}
    </div>
  );
}

export default Pokedex;
